        public CreateDoc(ILogger<CreateDoc> logger)
        {
            _logger = logger;

            // 同时支持本地 CLI 身份和云端托管身份
            var credential = new DefaultAzureCredential(new DefaultAzureCredentialOptions
            {
                ExcludeAzureCliCredential = false,       // 启用 az login 账户
                ExcludeVisualStudioCredential = false,   // 启用 VS 登录账户
                ExcludeEnvironmentCredential = false     // 启用环境变量
            });

            // 打印当前使用的令牌信息（仅调试用）
            try
            {
                var token = credential.GetToken(
                    new TokenRequestContext(new[] { "https://graph.microsoft.com/.default" }),
                    default);

                var jwt = token.Token;
                _logger.LogInformation("已获取访问令牌（可解码查看 upn/oid/tid）：{TokenSnippet}...", jwt.Substring(0, 40));
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "获取令牌失败，请检查本地是否已 az login 或 Azure Function 是否启用托管身份。");
            }

            _graph = new GraphServiceClient(credential);
        }

        [Function("CreateDoc")]
        public async Task<IActionResult> Run(
            [HttpTrigger(AuthorizationLevel.Anonymous, "get", "post")] HttpRequest req)
        {
            try
            {
                //_logger.LogInformation("开始从 SharePoint 获取数据...");
                //var listItem = await GetSharePointFieldsAsync();
                //_logger.LogInformation("已获取数据: {@ListItem}", listItem);

                String[] StyleXMLArray = { stylexml,TextBoxStyle };
                string[] DocumentXMLArray = { testxml1, testxml2, testxml3, TextBoxXml };

                _logger.LogInformation("开始生成 Word 文档...");
                var bytes = BuildWordBytes(DocumentXMLArray, StyleXMLArray);
                _logger.LogInformation("Word 文档生成完成。");

                // 1. 本地保存到 wwwroot
                var rootPath = System.IO.Path.Combine(Directory.GetCurrentDirectory(), "wwwroot");
                if (!Directory.Exists(rootPath))
                {
                    Directory.CreateDirectory(rootPath);
                }

                var fileName = $"Test_{DateTime.UtcNow:yyyyMMddHHmmss}.docx";
                var filePath = System.IO.Path.Combine(rootPath, fileName);

                await System.IO.File.WriteAllBytesAsync(filePath, bytes);
                // 生成 URL
                var fileUrl = $"{req.Scheme}://{req.Host}/{fileName}";

                return new FileContentResult(bytes, "application/vnd.openxmlformats-officedocument.wordprocessingml.document")
                {
                    FileDownloadName = $"Test_{DateTime.UtcNow:yyyyMMddHHmmss}.docx",

                };

                /*return new OkObjectResult(new
                {
                    output = "success",  // 这里就是新增的状态字段
                    fileName = $"Test_{DateTime.UtcNow:yyyyMMddHHmmss}.docx",
                    fileContent =fileUrl 
                });*/
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "生成文档失败");
                return new BadRequestObjectResult(new
                {
                    output = "fail", // 状态字段
                    error = ex.Message
                });
            }
        }

