        #region Word 生成
        private byte[] BuildWordBytes(string[] DocumentXmlArray, string[] styleXml)
        {
            using var ms = new MemoryStream();
            using (WordprocessingDocument wordDoc =
                   WordprocessingDocument.Create(ms, WordprocessingDocumentType.Document))
            {
                var mainPart = wordDoc.AddMainDocumentPart();
                mainPart.Document = new Document(new Body());

                Body body = mainPart.Document.Body!;

                #region code to generate doc
                // 标题
                //AppendTitle(body, Topic1); // 英文标题
                //AppendTitle(body, Topic2); // 中文标题

