        public static Run CreateTextBoxRun(int number, string name, string title)
        {
            // 唯一 Shape Id
            string shapeId = "_x0000_s" + Guid.NewGuid().ToString("N");

            // Shape（无边框）
            var textboxShape = new Shape()
            {
                Id = shapeId,
                Style = "width:200pt;height:80pt", // 高度容纳四行
                Type = "#_x0000_t202",
                Stroked = false // 无边框
            };

            // TextBox 内容
            var textbox = new DocumentFormat.OpenXml.Vml.TextBox();

            // 1. E-sign
            var paraEsign = new Paragraph(
                new ParagraphProperties(
                    new Justification() { Val = JustificationValues.Center }
                ),
                new Run(new Text($"{{E-sign{number} Placeholder}}"))
            );

            // 2. 横线
            var paraLine = new Paragraph(
                new ParagraphProperties(
                    new Justification() { Val = JustificationValues.Center },
                    new ParagraphBorders(
                        new BottomBorder()
                        {
                            Val = BorderValues.Single,
                            Size = 8
                        }
                    ),
                    new SpacingBetweenLines() { After = "0" }
                ),
                new Run(new Text(" "))
            );

            // 3. 名字
            var paraName = new Paragraph(
                new ParagraphProperties(
                    new Justification() { Val = JustificationValues.Center }
                ),
                new Run(new Text(name))
            );

            // 4. 职位
            var paraTitle = new Paragraph(
                new ParagraphProperties(
                    new Justification() { Val = JustificationValues.Center }
                ),
                new Run(new Text(title))
            );

            // 组装 TextBoxContent
            var textboxContent = new DocumentFormat.OpenXml.Wordprocessing.TextBoxContent(
                paraEsign,
                paraLine,
                paraName,
                paraTitle
            );

            textbox.Append(textboxContent);
            textboxShape.Append(textbox);

            // 用 Run 包装 Shape
            var run = new Run(new DocumentFormat.OpenXml.Wordprocessing.Picture(textboxShape));

            return run;
        }

        /// <summary>
        /// 按偶数左右、奇数最后一个居中方式添加 TextBox
        /// </summary>
        public static void AppendTextBoxesWithTabStops(Body body, string[] names, string[] titles)
        {
            int times = names.Length;

            for (int i = 0; i < times; i += 2)
            {
                var para = new Paragraph();

                // 段落属性：设置 TabStop
                var pPr = new ParagraphProperties(
                    new Tabs(
                        new TabStop()
                        {
                            Val = TabStopValues.Left,
                            Position = 0
                        },
                        new TabStop()
                        {
                            Val = TabStopValues.Right,
                            Position = 9000 // 右侧位置（1/20 pt，可调整）
                        }
                    )
                );

                para.Append(pPr);

                if (i + 1 < times)
                {
                    // 偶数行：左 + Tab + 右
                    para.Append(CreateTextBoxRun(i + 1, names[i], titles[i]));
                    para.Append(new TabChar());
                    para.Append(CreateTextBoxRun(i + 2, names[i + 1], titles[i + 1]));
                }
                else
                {
                    // 奇数最后一个：居中
                    pPr.Append(new Justification() { Val = JustificationValues.Center });
                    para.Append(CreateTextBoxRun(i + 1, names[i], titles[i]));
                }

                body.Append(para);
            }
        }
        #endregion

        #region Paragraph Context
        // 创建一个方法，生成带编号的段落
        Paragraph CreateNumberedParagraph(string text)
        {
            return new Paragraph(
                new ParagraphProperties(
                    new NumberingProperties(
                        new NumberingLevelReference() { Val = 0 },
                        new NumberingId() { Val = 1 }
                    ),
                    // 上下空行
                    new SpacingBetweenLines { Before = "200", After = "200" }
                ),
                new Run(new Text(text))
            );
        }
