        /// <summary>
        /// 去掉 xmlns 中多余的 <url ...> 标签，只保留真正的命名空间 URI。
        /// </summary>
        /// 

        #region Clean XMLs
        private static string CleanNamespaceUrls(string rawXml)
        {
            // 正则匹配 xmlns:xxx="<url ...>real-ns</url>" 的形式
            var pattern = @"xmlns:([^=]+)=""<url[^>]*>([^<]*)</url>""";
            return Regex.Replace(rawXml, pattern, @"xmlns:$1=""$2""", RegexOptions.IgnoreCase);
        }
