        public static void AppendElementFromXML(Body body, string[] rawXmlArray)
        {
            if (rawXmlArray == null || rawXmlArray.Length == 0) return;

            try
            {
                XNamespace w = "http://schemas.openxmlformats.org/wordprocessingml/2006/main";

                foreach (var rawXml in rawXmlArray)
                {
                    if (string.IsNullOrWhiteSpace(rawXml))
                        continue;

                    var cleanXml = CleanNamespaceUrls(rawXml);
                    var xDoc = XDocument.Parse(cleanXml);

                    foreach (var xe in xDoc.Root?.Element(w + "body")?.Elements() ?? Array.Empty<XElement>())
                    {
                        if (xe.Name.LocalName == "sectPr")
                            continue;   // 防止重复节属性

                        using var ms = new MemoryStream(Encoding.UTF8.GetBytes(xe.ToString(SaveOptions.DisableFormatting)));
                        using var rdr = OpenXmlReader.Create(ms);
                        while (rdr.Read())
                        {
                            if (rdr.ElementType != null)
                                body.Append(rdr.LoadCurrentElement());
                        }
                    }
                }
            }
            catch (Exception ex)
            {
                Console.WriteLine($"AppendElementFromXML 失败：{ex.Message}");
            }
        }

