        public static void ApplyStylesXml(WordprocessingDocument wordDoc,
                                  string[] rawXmlArray,
                                  bool preferExisting = true)
        {
            if (rawXmlArray == null || rawXmlArray.Length == 0) return;

            try
            {
                var stylePart = wordDoc.MainDocumentPart.StyleDefinitionsPart
                                ?? wordDoc.MainDocumentPart.AddNewPart<StyleDefinitionsPart>();

                if (stylePart.Styles == null)
                    stylePart.Styles = new Styles();

                var existingStyles = stylePart.Styles;
                XNamespace w = "http://schemas.openxmlformats.org/wordprocessingml/2006/main";

                foreach (var rawXml in rawXmlArray)
                {
                    if (string.IsNullOrWhiteSpace(rawXml)) continue;

                    var cleanXml = CleanNamespaceUrls(rawXml);
                    var xDoc = XDocument.Parse(cleanXml);

                    foreach (var xe in xDoc.Root?.Elements() ?? Array.Empty<XElement>())
                    {
                        var styleId = xe.Attribute(w + "styleId")?.Value;

                        if (!string.IsNullOrEmpty(styleId))
                        {
                            //关键修复：用 .Value 比较
                            var oldStyle = existingStyles.Elements<Style>()
                                                         .FirstOrDefault(s => s.StyleId?.Value == styleId);

                            if (oldStyle != null)
                            {
                                if (preferExisting) continue;
                                oldStyle.Remove();
                            }
                        }

                        using var ms = new MemoryStream(
                            Encoding.UTF8.GetBytes(xe.ToString(SaveOptions.DisableFormatting)));
                        using var rdr = OpenXmlReader.Create(ms);
                        while (rdr.Read())
                        {
                            if (rdr.ElementType != null)
                                existingStyles.Append(rdr.LoadCurrentElement());
                        }
                    }
                }

                stylePart.Styles.Save();
            }
            catch (Exception ex)
            {
                Console.WriteLine($"ApplyStylesXml 失败：{ex.Message}");
            }
        }


